import React, { useState } from 'react';
import DescriptorSelection from './DescriptorSelection'; // Asegúrate de importar el componente correcto
import Modal from 'react-modal';
import data from './descriptors.json'; // Importar el JSON

Modal.setAppElement('#root');

const DefaultValuesManager = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [defaultValues, setDefaultValues] = useState(data.defaultValues);

    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    const handleSaveDefaultValues = (newDefaultValues) => {
        setDefaultValues(newDefaultValues);
        toggleModal();
        // Aquí puedes agregar la lógica para guardar los cambios en el JSON
    };

    return (
        <div>
            <button onClick={toggleModal}>Actualizar valores por defecto</button>
            <Modal isOpen={isModalOpen} onRequestClose={toggleModal}>
                <h2>Modificar valores por defecto</h2>
                <DescriptorSelection 
                    descriptorList={data.descriptorList}
                    selectedDescriptors={defaultValues} 
                    onDescriptorChange={handleSaveDefaultValues} 
                />
                <button onClick={toggleModal}>Cerrar</button>
            </Modal>
        </div>
    );
};

export default DefaultValuesManager;
