import React, { useState } from 'react';
import DescriptorSelection from './DescriptorSelection'; // Asegúrate de importar el componente correcto
import Modal from 'react-modal';

// Asegúrate de llamar a Modal.setAppElement en algún lugar para accesibilidad
Modal.setAppElement('#root');

const DefaultValuesManager = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [defaultValues, setDefaultValues] = useState({
        'Diferencias Pesadas': { 'peso': '5' },
        'Fuzzy': { 'threshold': '120' },
        'Wavelet Entropy': { 'wavelet': 'db2', 'level': '5' },
        'High Low Ratio': { 'fs': '1.0' },
        'Filtro Bajo': { 'fmin': '0.015', 'fmax': '0.05', 'at_paso': '1', 'at_rechazo': '40', 'fs': '1.0' },
        'Filtro Medio': { 'fmin': '0.05', 'fmax': '0.25', 'at_paso': '1', 'at_rechazo': '40', 'fs': '1.0' },
        'Filtro Alto': { 'fmin': '0.025', 'fmax': '0.4', 'at_paso': '1', 'at_rechazo': '40', 'fs': '1.0' },
    });

    // Función para abrir/cerrar el modal
    const toggleModal = () => {
        setIsModalOpen(!isModalOpen);
    };

    // Función para guardar los nuevos valores por defecto desde el modal
    const handleSaveDefaultValues = (newDefaultValues) => {
        setDefaultValues(newDefaultValues); // Actualizamos los valores por defecto
        toggleModal(); // Cerrar el modal después de guardar
    };

    return (
        <div>
            {/* Botón para abrir el modal */}
            <button onClick={toggleModal}>Actualizar valores por defecto</button>

            {/* Modal para modificar los valores por defecto */}
            <Modal isOpen={isModalOpen} onRequestClose={toggleModal}>
                <h2>Modificar valores por defecto</h2>
                <DescriptorSelection 
                    defaultValues={defaultValues} 
                    onSave={handleSaveDefaultValues} 
                />
                <button onClick={toggleModal}>Cerrar</button>
            </Modal>
        </div>
    );
};

export default DefaultValuesManager;
